                \end{bmatrix}
                \begin{bmatrix}
                 0 & 1 & 1 & 2 \\
                 1 & 1 & 3 & 6 \\
                 0 & 1 & 2 & 1 \\
                 1 & 0 & 3 & 4
                \end{bmatrix}
            .

